import React, { useState, useEffect } from 'react'
import useIpfsApi, { IpfsConfig } from '../../hooks/use-ipfs-api'
import { formatBytes, addToIpfs } from '../../utils'
import { site } from '../../site.config'
import Dropzone from '../atoms/Dropzone'
import styles from './Add.module.css'
import Spinner from '../atoms/Spinner'
import Button from '../atoms/Button'
import Input from '../atoms/Form/Input'

const { hostname, port, protocol } = new URL(site.ipfsNodeUri)

const ipfsConfig: IpfsConfig = {
  protocol: protocol.replace(':', ''),
  host: hostname,
  port: port || '443'
}

export default class Add {
  
  // const [fileSizeReceived] = 

  ipfs = useIpfsApi(ipfsConfig).ipfs;
  isIpfsReady = useIpfsApi(ipfsConfig).isIpfsReady;
  ipfsError = useIpfsApi(ipfsConfig).ipfsError;
  fileHash = useState()[0];
  setFileHash = useState()[1];
  loading = useState(false)[0];
  setLoading = useState(false)[1];
  message = useState()[0];
  setMessage = useState()[1];
  error = useState()[0];
  setError = useState()[1];
  fileSize = useState()[0];
  setFileSize = useState()[1];
  fileSizeReceived = useState('')[0];
  /**
   *
   */
  constructor() {
    useEffect(() => {
    //  this.setMessage(`Adding to IPFS<br />  <small>${this.fileSizeReceived || 0}/${this.fileSize}</small><br />`)
    }, [this.fileSize, this.fileSizeReceived])
  }

 

   async handleOnDrop(acceptedFiles: File[]) {
    if (!acceptedFiles[0]) return

    this.setLoading(true)
    this.setError(null)

    const totalSize = formatBytes(acceptedFiles[0].size, 0)
    this.setFileSize(totalSize)

    try {
      const cid = await addToIpfs(acceptedFiles, ipfs)
      if (!cid) return
      this.setFileHash(cid)
      this.setLoading(false)
    } catch (error) {
      //this.setError(`Adding to IPFS failed: ${error.message}`)
      return null
    }
  }
 render(){
  return (
    <div className={styles.add}>

      <div className={styles.topDiv}>
        <Input
          key={'Name'}
          name={'Name'}
          label={'Asset Name'}
          placeholder={'Enter The Name Of Your Asset'}
          required={true}
          type={'text'}
        />
      </div>

      <div className={styles.topDiv}>
        <Input
          key={'Description'}
          name={'Description'}
          label={'Asset Description'}
          placeholder={'Enter The Description Of Your Asset'}
          required={true}
          type={'text area'}
        />
      </div>


      <div className={styles.lastInputDiv}>
        <Input
          key={'price'}
          name={'price'}
          label={'Asset price'}
          placeholder={'Enter The Price Of Your Asset'}
          required={true}
          type={'number'}
        />
      </div>

      {this.loading ? (
        <Spinner message={this.message} />
      ) : this.fileHash ? (
        <a
          target="_blank"
          rel="noopener noreferrer"
          href={`${site.ipfsGateway}/ipfs/${this.fileHash}`}
        >
          ipfs:{this.fileHash}
        </a>
      ) : (
            <div style={{ width: '100%' }}>
              <Dropzone

                multiple={false}
                handleOnDrop={this.handleOnDrop}
                disabled={!this.isIpfsReady}
                error={this.error || this.ipfsError}
              />
            </div>
          )}
      <div style={{ marginTop: '2rem' }} className={styles.buttons}>
        <Button primary>Publish Data</Button>
      </div>
    </div>)
}
}
