import React, { Component } from 'react'
import Route from '../components/templates/Route'
import styles from './Search.module.scss'
import Spinner from '../components/atoms/Spinner'
import Asset from '../components/molecules/Asset'


class Marketplace extends Component {

    public state = {
        results: [{ id: 1, name: "Name", datePublished:"sasasa" }, {id: 1, name: "Name", datePublished:"sasasa" }, { id: 1, name: "Name", datePublished:"sasasa"}],
        isLoading: true
    }
    private searchAssets = async () => {
        const searchQuery = {
            offset: this.state.offset,
            page: this.state.currentPage,
            query: {
                text: [this.searchTerm],
                price: [-1, 1]
            },
            sort: {
                datePublished: 1
            }
        }

        const search = await this.context.ocean.aquarius.queryMetadata(
            searchQuery
        )
        this.setState({
            results: search.results,
            totalResults: search.totalResults,
            totalPages: search.totalPages,
            currentPage: search.page, // first page is always 0 in response
            isLoading: false
        })
    }
    public renderResults = () =>
        this.state.isLoading ? (
            <Spinner message="loading..." />
        ) : this.state.results && this.state.results.length ? (
            <div className={styles.results}>
                {this.state.results.map((asset: any) => (
                    <Asset key={asset.id} asset={asset} />
                ))}
            </div>
        ) : (
                    <div>No data sets found.</div>
                )


    public render() {
        return <Route
            title={'Marketplace'}
        >
            {this.renderResults()}

        </Route>
    }
}

export default Marketplace
